mapper.update:
UPDATE `article`
user_id = #{userId}, title = #{title}, content = #{content}, images = #{images}, classify = #{classify}, mode = #{mode}, read_count = #{readCount}, collection_count = #{collectionCount}, comment_count = #{commentCount}, thumbs_up_count = #{thumbsUpCount},
WHERE id = #{id} AND deleted = 0;

mapper.select:
SELECT a.*, ru.article_id > 0 as `isLike` FROM `article` a LEFT JOIN `r_user_like_article`  ru ON a.id = ru.article_id and ru.user_id = #{query.userId}

    AND a.deleted = 0

        AND a.author_id = #{query.authorId}
        AND a.title = #{query.title}
        AND a.content = #{query.content}
        AND a.images = #{query.images}
        AND a.classify like "%"#{query.classify}"%"
        AND a.mode = #{query.mode}
        AND a.read_count = #{query.readCount}
        AND a.collection_count = #{query.collectionCount}
        AND a.comment_count = #{query.commentCount}
        AND a.thumbs_up_count = #{query.thumbsUpCount}

SELECT a.*, ru.article_id > 0 as `isLike` FROM `article` a LEFT JOIN `r_user_like_article`  ru ON a.id = ru.article_id and ru.user_id = #{userId}

    AND a.deleted = 0
    AND ( a.title like "%"#{search}"%" OR a.content like "%"#{search}"%" OR a.classify like "%"#{search}"%")
